window.addEventListener("load",function(){window.location.hash=""});var Router=function(){this.routes=[],this.listening=!1};Router.prototype.addRoute=function(a,b){try{if("string"!==(typeof a).toLowerCase()||"function"!==(typeof b).toLowerCase())throw new Error("Invalid arguments at 'addRoute'!");this.routes.push({route:a,cb:b})}catch(a){return console.error(a),null}return this.routes},Router.prototype.listen=function(){try{let a=this.routes;if(this.listening===!0)throw new Error("Listen has been called previously!");window.addEventListener("hashchange",function(){if(event.preventDefault(),""!==window.location.hash){let c=(window.location.hash,window.location.hash.slice(1));for(let d of a)if(c===d.route)return d.cb(),!0;throw new Error("There is no such route by name: "+c)}})}catch(a){return console.error(a),null}},window.$router=function(a,b){"use strict";return new Router}.bind(null,window,document);